import React from 'react';
import Axios from 'axios';

class Login extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            id : ''
        ,   pw : ''
        ,   data : []
        }
    }
 
    saveId = () => {
        window.sessionStorage.setItem("ID", this.state.id);
    }
    
