import React from 'react';

class Login extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            id : ''
        ,   pw : ''
        }
    }

    saveId = () => {
        window.sessionStorage.setItem("ID", this.state.id);
    }
    
    static getDerivedStateFromProps(props, state){
        console.log("tlqkf");

        var grpcjs = require('@grpc/grpc-js');
        var protoLoader = require('@grpc/proto-loader');

        var PROTO_PATH_TEST = __dirname  + '/LoginService.proto';
        var packageDefinition_Test = protoLoader.loadSync(
        PROTO_PATH_TEST,
        {keepCase: true,
        longs: String,
        enums: String,
        defaults: true,
        oneofs: true
        });
        var protoDescriptor_Test = grpcjs.loadPackageDefinition(packageDefinition_Test);
        var client_Test = new protoDescriptor_Test.LoginService.UserInfo('203.245.41.17:50052', grpcjs.credentials.createInsecure());

        // 방식 1
        var data1 = protoDescriptor_Test.LoginService.Request_UserInfo
        data1.Userid = "testuser"
        data1.UserPassword = "1234"

        // 방식 2
        var data2 = {
        UserId : "testuser", 
        UserPassword : "1234", 
        }

        client_Test.Login(data2, function(err, data) {
        try
        {
            console.log('error : ', err);
            console.log(data);
            console.log(data.Message);
        }
        catch(ex)
        {
            console.log(ex)
        }
        });
    }
    
    render() {
        return (
            <>
                <div>ID : <input type='text' name="id" value={this.state.id} onChange={({target : { value }}) => this.setState({ id : value })} /></div>
                <div>PW : <input type='password' name="pw" value={this.state.pw} onChange={({target : { value }}) => this.setState({ pw : value })} /></div>
                <div><button type="button" onClick={this.saveId}>로그인</button></div>
            </>
        );
    }
}

export default Login;
