import React from 'react';
import axios from 'axios';

import TableRow from '@material-ui/core/TableRow';
import TableCell from '@material-ui/core/TableCell';
class Login extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            id : ''
        ,   pw : ''
        ,   resData : []
        }
    }
 
    saveId = () => {
        window.sessionStorage.setItem("ID", this.state.id);
    }
    
    // componentDidMount() {
    //     this.fnFetchRes();
    // };

    // fnFetchRes = async () => {
    //     const myRes = await fetch('/api')
    //       //.then(res => res.json())
    //       .then(function(response) {
    //         console.log(response.json());
    //         return response.json()})
    //       .then(data => this.setState({title: data.title}))
    //       .catch(error => console.error('Error: ', error));

    //       return myRes;
    // }

    // componentDidMount = () => {
    //     const resCallApi = this.callApi();

    //     console.log(resCallApi);
    // }

    // callApi = async () => {
    //     let url = '/api';

    //     Axios.post(url).then((response) => {
    //         console.log(response);
    //     });
    // }

    componentDidMount = () => {
        this.callApi();
    }

    callApi = async () => {
        await axios({
            method: "get",
            url: "/api/TotalCategory_List",
            responseType: "type"
        }).then(({ data }) => {
            this.setState({ 
                //resData: JSON.stringify(data.Data)
                resData: data.Data
            });
            
            console.log(this.state.resData);
        }).catch(function (error) {
            // 오류발생시 실행
        });
    }

    render() {
        return (
            <>
                <div>ID : <input type='text' name="id" value={this.state.id} onChange={({target : { value }}) => this.setState({ id : value })} /></div>
                <div>PW : <input type='password' name="pw" value={this.state.pw} onChange={({target : { value }}) => this.setState({ pw : value })} /></div>
                <div><button type="button" onClick={this.saveId}>로그인</button></div>
                <div>
                    <ul>
                        {this.state.resData.map((data, i) => {
                            <TableRow>
                                return (
                                    <TableCell>{data.Category1}</TableCell>
                                    <TableCell>{data.Category2}</TableCell>
                                    <TableCell>{data.Category3}</TableCell>
                                );
                            </TableRow>
                        })}
                    </ul>
                </div>
            </>
        );
    }
}

export default Login;
