import React from 'react';
import axios from 'axios';

class Login extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            id : ''
        ,   pw : ''
        ,   data : ''
        }
    }
 
    saveId = () => {
        window.sessionStorage.setItem("ID", this.state.id);
    }
    
    // componentDidMount() {
